#include <stdio.h>
void morning();
void afternoon();
void night();
int main()
{
    // C program to print good morning,good afternoon,good night by using FUNCTION

    // Function in C
    morning();
    afternoon();
    night();
    return 0;
}

void morning()
{
    printf("hello have a good morning\n");
}
void afternoon()
{
    printf("hello have a good afternoon\n");
}
void night()
{
    printf("hello have a good night\n");
}